import React, { useEffect, useRef} from "react";
import { Button, Dialog, IconButton, Typography } from "@material-ui/core";
import {
  createStyles,
  withStyles,
} from "@material-ui/core/styles";
import MuiDialogTitle from "@material-ui/core/DialogTitle";
import MuiDialogContent from "@material-ui/core/DialogContent";
import MuiDialogActions from "@material-ui/core/DialogActions";
import CloseIcon from "@material-ui/icons/Close";

import TextField from '@material-ui/core/TextField';


const styles = (theme) =>
  createStyles({
    root: {
      margin: 0,
      padding: theme.spacing(2)
    },
    closeButton: {
      position: "absolute",
      right: theme.spacing(1),
      top: theme.spacing(1),
      color: theme.palette.grey[500]
    }
  });


const DialogTitle = withStyles(styles)((props) => {
  const { children, classes, onClose, ...other } = props;
  return (
    <MuiDialogTitle disableTypography className={classes.root} {...other}>
      <Typography variant="h6">{children}</Typography>
      {onClose && (
        <IconButton
          aria-label="close"
          data-testid="close-modal"
          className={classes.closeButton}
          onClick={onClose}
        >
          <CloseIcon />
        </IconButton>
      )}
    </MuiDialogTitle>
  );
});

const DialogContent = withStyles((theme) => ({
  root: {
    padding: theme.spacing(2)
  }
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(1)
  }
}))(MuiDialogActions);

export const FormModal = ({
  //onSubmit,
  onCloseModal,
  maxWidth,
  modelData,
  //children,
  cancelButton,
  //inputVal, 
  //setInputVal,
  //register,
}) => {

  const titleRef=useRef();
  const bodyRef=useRef();
const [inputVal, setInputVal] = React.useState(modelData); 
const [saveButton, setSaveButton] = React.useState(true); 
useEffect(() => {setInputVal(modelData)},[modelData]); 
// useEffect(() => {
//   const newTitle = titleRef.current.value;
//   const newBody = bodyRef.current.value;
//   if(newTitle !== modelData.title ||  newBody !== modelData.body){
//     setSaveButton(false);
//   }},[inputVal]); 



// const newTitle = titleRef.current.value;
// const newBody = bodyRef.current.value;
// //setInputVal({...inputVal, tittle: titleRef.current.value, body: newBody});
// if(newTitle !== modelData.title ||  newBody !== modelData.body){
//   setSaveButton(false);
// }

// const newTitle = titleRef.current.value;
//   const newBody = bodyRef.current.value;
//   console.log("newTitle",newTitle);

const onSubmit = (e) => {
  const newTitle = titleRef.current.value;
  const newBody = bodyRef.current.value;
  console.log("newTitle",newTitle);
  // if(newTitle !== modelData.title ||  newBody !== modelData.body){
  //   setSaveButton(false);
  // }
  e.preventDefault();
  onCloseModal();
};

console.log("inputVal",inputVal);

  return (
    <Dialog
      onClose={onCloseModal}
      aria-labelledby="customized-dialog-title"
      open={modelData.open}
      fullWidth
      maxWidth={maxWidth}
      data-testid="form-modal"
    >
      <form onSubmit={onSubmit}>
        <DialogTitle id="form-dialog-title" onClose={onCloseModal}>
          Dialog Box
        </DialogTitle>
        <DialogContent dividers>
        <TextField
        id="outlined-multiline-static"
        fullWidth
        multiline
        minRows={3}
        variant="outlined"
        defaultValue={inputVal.title}
        inputRef={titleRef}
        label="title"
        name="body"
        onChange={() => setInputVal({...inputVal, tittle: titleRef.current.value})}
      />
      <DialogContent />
      <TextField
        id="outlined-multiline-static"
        fullWidth
        multiline
        minRows={3}
        variant="outlined"
        defaultValue={inputVal.body} 
        inputRef={bodyRef}
        name="Content"
        label="Content"
      />
      </DialogContent>
        <DialogActions>
          {cancelButton && (
            <Button
              onClick={onCloseModal}
              color="secondary"
              data-testid="cancel-button"
            >
              Cancel
            </Button>
          )}
          <Button
            type="submit"
            autoFocus
            color="primary"
            data-testid="submit-button"
            disabled={false}
          >
            Save changes
          </Button>
        </DialogActions>
      </form>
    </Dialog>
  );
};
