import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import { withStyles } from "@material-ui/core/styles";
import Avatar from "@material-ui/core/Avatar";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Divider from "@material-ui/core/Divider";
import Typography from "@material-ui/core/Typography";
// import MoreVertIcon from "@material-ui/icons/MoreVert";
import EditIcon from "@material-ui/icons/Edit";
import CardHeader from "@material-ui/core/CardHeader";
import IconButton from "@material-ui/core/IconButton";
import "./index.css";
import {styles} from './shared/styles';
import {useDataApi} from './utils/useDataApi'
import Pagination from "@material-ui/lab/Pagination";


function App({ classes }) {
  const [{ data: datas, isLoading, isError }] = useDataApi();
  const firstIndex = 0;
  console.log(datas);

  const pageSize = 10;
  const [page, setPage] = useState(1);
  const [data, setData] = useState(datas?.slice(firstIndex, pageSize));

  useEffect(() => {
    setData(datas?.slice(0, pageSize));
  }, [datas,pageSize]);

  const handleChange = (event, value) => {
    setPage(value);
    setData(datas?.slice(firstIndex + pageSize * (value - 1), pageSize * value));
  };


  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => { 
    console.log("Aishwarya");
    setShow(true);
  };

  return (
    <div className="App">
      {data?.map(({body,id,title},i) => 
      <Card className={classes.card} key={`${title}${id}`}>
        <CardContent className={classes.content}>
          <CardHeader
          action={
            <div>
              <IconButton aria-label="settings" onClick={handleShow}>
                <EditIcon />
              </IconButton>
            </div>
          }
          title={title}  
        />
          <Typography
            className={"MuiTypography--subheading"}
            variant={"caption"}
          >
            {body}
          </Typography>
        </CardContent>
      </Card>
)}

<Typography>Page: {page}</Typography>
<Pagination
        count={Math.ceil(datas?.length / pageSize)}
        page={page}
        onChange={handleChange}
      />
    </div>
  );
}

const WrappedApp = withStyles(styles)(App);

const rootElement = document.getElementById("root");
ReactDOM.render(<WrappedApp />, rootElement);
