import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import { withStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Divider from "@material-ui/core/Divider";
import Typography from "@material-ui/core/Typography";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import CardHeader from "@material-ui/core/CardHeader";
import IconButton from "@material-ui/core/IconButton";
import "./index.css";
import {styles} from './shared/styles';
import {useDataApi} from './utils/useDataApi'
import Pagination from "@material-ui/lab/Pagination";
import { FormModal } from "./FormModal";


function App({ classes }) {
  const [{ data: datas, isLoading, isError }] = useDataApi();
  const firstIndex = 0;
  const pageSize = 10;

  const [page, setPage] = React.useState(1);
  const [data, setData] = React.useState(datas?.slice(firstIndex, pageSize));
  React.useEffect(() => {
    setData(datas?.slice(0, pageSize));
  }, [datas,pageSize]);

  const handleChange = (event, value) => {
    setPage(value);
    setData(datas?.slice(firstIndex + pageSize * (value - 1), pageSize * value));
  };

  const [openModal, setOpenModal] = useState({open: false});
  const onCloseModal = () => {
    setOpenModal({open: false });
  };
  const handleOpenModal = (item,index) => {
    setOpenModal({...item,open: true, index: index});
  };

  const updateData = (updateVal) => {
    console.log("updateVal",updateVal);
    const data1=data.filter(item => item.id !== updateVal.id);
    // const data1=data.filter(item => {
    //   // console.log("item.id",item.id);
    //   // console.log("updateVal.id",updateVal.id);
    //   if(item.id === updateVal.id){
    //     console.log("item.id",updateVal.body);
    //     console.log("updateVal.id",updateVal.title);
    //     // return {body: updateVal.body, title: updateVal.title};
    //     data.push({...item,body: updateVal.body, title: updateVal.title});
    //     data.pop(item);
    //   }
    //   // else{
    //   //   return item;
    //   // }

    // });
    data1.splice(updateVal.index, 0, updateVal);
    setData
  };

  return (
    <div className="App">
      {data?.map((item,index) => 
      <Card className={classes.card} key={`${item.title}${item.id}`}>
        <CardContent className={classes.content}>
          <CardHeader
          action={
            <div>
              <IconButton aria-label="settings" onClick={() => handleOpenModal(item,index)}>
                <MoreVertIcon />
              </IconButton>
            </div>
          }
          title={item.title}  
        />
          <Typography
            className={"MuiTypography--subheading"}
            variant={"caption"}
          >
            {item.body}
          </Typography>
        </CardContent>
      </Card>
)}

<FormModal
          title="Form modal"
          onCloseModal={onCloseModal}
          maxWidth="sm"
          modelData={openModal}
          updateData={updateData}
          cancelButton
        />

<Typography>Page: {page}</Typography>
<Pagination
        count={Math.ceil(datas?.length / pageSize)}
        page={page}
        onChange={handleChange}
      />

    </div>
  );
}

const WrappedApp = withStyles(styles)(App);

const rootElement = document.getElementById("root");
ReactDOM.render(<WrappedApp />, rootElement);
