import React, { FC , useEffect, useRef} from "react";
import { Button, Dialog, IconButton, Typography } from "@material-ui/core";
import {
  createStyles,
  Theme,
  withStyles,
  WithStyles,
  makeStyles
} from "@material-ui/core/styles";
import MuiDialogTitle from "@material-ui/core/DialogTitle";
import MuiDialogContent from "@material-ui/core/DialogContent";
import MuiDialogActions from "@material-ui/core/DialogActions";
import CloseIcon from "@material-ui/icons/Close";

import TextField from '@material-ui/core/TextField';


const styles = (theme) =>
  createStyles({
    root: {
      margin: 0,
      padding: theme.spacing(2)
    },
    closeButton: {
      position: "absolute",
      right: theme.spacing(1),
      top: theme.spacing(1),
      color: theme.palette.grey[500]
    }
  });


const DialogTitle = withStyles(styles)((props) => {
  const { children, classes, onClose, ...other } = props;
  return (
    <MuiDialogTitle disableTypography className={classes.root} {...other}>
      <Typography variant="h6">{children}</Typography>
      {onClose && (
        <IconButton
          aria-label="close"
          data-testid="close-modal"
          className={classes.closeButton}
          onClick={onClose}
        >
          <CloseIcon />
        </IconButton>
      )}
    </MuiDialogTitle>
  );
});

const DialogContent = withStyles((theme) => ({
  root: {
    padding: theme.spacing(2)
  }
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(1)
  }
}))(MuiDialogActions);

const useFormModalStyles = makeStyles((theme) =>
  createStyles({
    root: {
      "& .MuiTextField-root": {
        margin: theme.spacing(1)
      }
    }
  })
);


export const FormModal = ({
  title,
  onSubmit,
  onCloseModal,
  maxWidth,
  modelData,
  children,
  cancelButton,
  register,
}) => {

const [inputVal, setInputVal] = React.useState(modelData); 

useEffect(() => {setInputVal(modelData)},[modelData])  

const titleRef=useRef();
const bodyRef=useRef();

const onSubmit1 = (e) => {
  setInputVal({...inputVal, tittle: titleRef.current.value, body: bodyRef.current.value});
  e.preventDefault();
  onCloseModal();
};

  return (
    <Dialog
      onClose={onCloseModal}
      aria-labelledby="customized-dialog-title"
      open={modelData.open}
      fullWidth
      maxWidth={maxWidth}
      data-testid="form-modal"
    >
      <form onSubmit={onSubmit1}>
        <DialogTitle id="form-dialog-title" onClose={onCloseModal}>
          {title}
        </DialogTitle>
        <DialogContent dividers>
        <TextField
        id="outlined-multiline-static"
        fullWidth
        multiline
        minRows={30}
        variant="outlined"
        defaultValue={inputVal.title}
        inputRef={titleRef}
        label={inputVal.title}
        name="body"
      />
      <DialogContent />
      <TextField
        id="outlined-multiline-static"
        fullWidth
        multiline
        minRows={30}
        variant="outlined"
        defaultValue={inputVal.body} 
        inputRef={bodyRef}
        label={inputVal.body}
        name="body"
      />
      </DialogContent>
        <DialogActions>
          {cancelButton && (
            <Button
              onClick={onCloseModal}
              color="secondary"
              data-testid="cancel-button"
            >
              Cancel
            </Button>
          )}
          <Button
            type="submit"
            autoFocus
            color="primary"
            data-testid="submit-button"
          >
            Save changes
          </Button>
        </DialogActions>
      </form>
    </Dialog>
  );
};
