import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import { withStyles } from "@material-ui/core/styles";
import Avatar from "@material-ui/core/Avatar";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Divider from "@material-ui/core/Divider";
import Typography from "@material-ui/core/Typography";
// import MoreVertIcon from "@material-ui/icons/MoreVert";
import EditIcon from "@material-ui/icons/Edit";
import CardHeader from "@material-ui/core/CardHeader";
import IconButton from "@material-ui/core/IconButton";
import "./index.css";
import {styles} from './shared/styles';
import {useDataApi} from './utils/useDataApi'
import Pagination from "@material-ui/lab/Pagination";
import { FormModal } from "./FormModal";
import { useForm } from "react-hook-form";


export const FormExample = ({ register }) => {
  return (
    <>
      <TextField inputRef={register} label="Title" name="title" fullWidth />
      <TextField
        id="outlined-multiline-static"
        label="Description"
        inputRef={register}
        name="description"
        fullWidth
        multiline
        rows={3}
        variant="outlined"
      />
    </>
  );
};

function App({ classes }) {
  const [{ data: datas, isLoading, isError }] = useDataApi();
  const firstIndex = 0;
  console.log(datas);

  const pageSize = 10;
  const [page, setPage] = useState(1);
  const [data, setData] = useState(datas?.slice(firstIndex, pageSize));

  useEffect(() => {
    setData(datas?.slice(0, pageSize));
  }, [datas,pageSize]);

  const handleChange = (event, value) => {
    setPage(value);
    setData(datas?.slice(firstIndex + pageSize * (value - 1), pageSize * value));
  };  

  const [openModal, setOpenModal] = useState(false);
  const onCloseModal = () => {
    console.log("close");
    setOpenModal(false);
  };
  const handleOpenModal = () => {
    setOpenModal(true);
  };
  const onSubmit = (data) => {
    console.log("submit");
    console.log(data);
    onCloseModal();
  };
  const { register, handleSubmit } = useForm();
  return (
    <div className="App">
      {data?.map(({body,id,title},i) => 
      <Card className={classes.card} key={`${title}${id}`}>
        <CardContent className={classes.content}>
          <CardHeader
          action={
            <div>
              <IconButton aria-label="settings" onClick={handleOpenModal}>
                <EditIcon />
              </IconButton>
            </div>
          }
          title={title}  
        />
          <Typography
            className={"MuiTypography--subheading"}
            variant={"caption"}
          >
            {body}
          </Typography>
        </CardContent>
      </Card>
)}

<FormModal
        title="Form modal"
        onSubmit={() => handleSubmit(onSubmit)}
        onCloseModal={onCloseModal}
        maxWidth="sm"
        isOpen={openModal}
        cancelButton
      >
        <FormExample register={register} />
      </FormModal>

<Typography>Page: {page}</Typography>
<Pagination
        count={Math.ceil(datas?.length / pageSize)}
        page={page}
        onChange={handleChange}
      />
    </div>
  );
}

const WrappedApp = withStyles(styles)(App);

const rootElement = document.getElementById("root");
ReactDOM.render(<WrappedApp />, rootElement);
