import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import { withStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Divider from "@material-ui/core/Divider";
import Typography from "@material-ui/core/Typography";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import CardHeader from "@material-ui/core/CardHeader";
import IconButton from "@material-ui/core/IconButton";
import "./index.css";
import {styles} from './shared/styles';
import {useDataApi} from './utils/useDataApi'
import Pagination from "@material-ui/lab/Pagination";
import { FormModal } from "./FormModal";


function App({ classes }) {
  const [{ data: datas, isLoading, isError }] = useDataApi();
  const firstIndex = 0;
  const pageSize = 10;
  useEffect(()=> {setActualData(datas)},[datas]);

  const[actualData, setActualData]= useState(null);

  const [page, setPage] = React.useState(1);
  const [data, setData] = React.useState(actualData?.slice(firstIndex, pageSize));

  React.useEffect(() => {
    setData(actualData?.slice(0, pageSize));
  }, [actualData]);

  const handleChange = (event,value) => {
    setPage(value);
    setData(actualData?.slice(firstIndex + pageSize * (value - 1), pageSize * value));
  };

  const [openModal, setOpenModal] = useState({open: false});
  const onCloseModal = () => {
    setOpenModal({open: false });
  };
  const handleOpenModal = (item,index) => {
    setOpenModal({...item,open: true, index: index});
  };

  const updateData = (updateVal) => {
    const data1=actualData.filter(item => item.id !== updateVal.id);
    //data1.splice(updateVal.index, 0, updateVal);
    // setActualData(data1);


    let data3 = data3.splice(updateVal.index, 0, updateVal);
    setData(data3);

    let index;
    for(let i = 0; i < actualData.length; i++) {
      if(actualData[i].id === updateVal.id) {
          index = i;
          break;
      }
  }
  let data4 = data1.splice(index, 0, updateVal);
  setActualData(data4);
  // setPage(page);
  // setData(actualData?.slice(firstIndex + pageSize * (page - 1), pageSize * page));


    // const data3 = [updateVal];
    //   const data212 = actualData;
    //   data3.map(x => {
    //      let index = data212.findIndex(d=> d.id === x.id)
    //      data212[index] = x; 
    //  })
    //  setActualData(data212);
  };

  return (
    <div className="App">
      {data?.map((item,index) => 
      <Card className={classes.card} key={`${item.title}${item.id}`}>
        <CardContent className={classes.content}>
          <CardHeader
          action={
            <div>
              <IconButton aria-label="settings" onClick={() => handleOpenModal(item,index)}>
                <MoreVertIcon />
              </IconButton>
            </div>
          }
          title={item.title}  
        />
          <Typography
            className={"MuiTypography--subheading"}
            variant={"caption"}
          >
            {item.body}
          </Typography>
        </CardContent>
      </Card>
)}

<FormModal
          title="Form modal"
          onCloseModal={onCloseModal}
          maxWidth="sm"
          modelData={openModal}
          updateData={updateData}
          cancelButton
        />

<Typography>Page: {page}</Typography>
<Pagination
        count={Math.ceil(actualData?.length / pageSize)}
        page={page}
        onChange={handleChange}
      />

    </div>
  );
}

const WrappedApp = withStyles(styles)(App);

const rootElement = document.getElementById("root");
ReactDOM.render(<WrappedApp />, rootElement);
